const full_blog_content = $('generate content').first().json.message.content.fullBlogContent;
const title = $('Google Sheets - get titles').first().json['title'];
const images = $input.first().json.data;

let fullMarkdownContent = full_blog_content;

images.forEach((item, idx) => {
  const placeholder = `<PLACE YOUR IMAGE[${idx + 1}] LINK>`;
  fullMarkdownContent = fullMarkdownContent.replace(placeholder, item.source_url);
});

return {
  json: {
    title: title,
    full_blog_content: fullMarkdownContent,
  }
};

const inputText = $input.first().json.message.content;

const jsonStartIndex = inputText.indexOf('```json\n{');
const jsonEndIndex = inputText.indexOf('}\n```');

let mainBodyJson = {};
let footnotesText = '';

if (jsonStartIndex !== -1 && jsonEndIndex !== -1) {
  const jsonString = inputText.substring(jsonStartIndex + '```json\n'.length, jsonEndIndex + 1);
  const parsedJson = JSON.parse(jsonString);
  mainBodyJson = parsedJson.main_body || {};
  footnotesText = parsedJson.footnotes || '';
}

let articleContent = $('Code').first().json.full_blog_content

for (const term in mainBodyJson) {
  if (Object.hasOwnProperty.call(mainBodyJson, term)) {
    articleContent = articleContent.replace(term, mainBodyJson[term]);
  }
}

let fullArticle = articleContent;
if (footnotesText) {
  fullArticle += '\n\n---\n\n' + footnotesText;
}

return [{
  json: {
    title: $('Code').first().json.title,
    full_blog_content: fullArticle
  }
}];

